import Web3 from 'web3'
import { _axios } from '@/plugins/axios'

let _account = ''
let _web3 = null

function getWalletProvider(walletName) {
  const ethereum = window.ethereum || {}
  const providers = ethereum.providers || []
  let provider = ethereum

  switch (walletName) {
    case 'TokenPocket':
      provider =
        providers.find((_provider) => _provider.isTokenPocket) ||
        (ethereum.isTokenPocket && ethereum)
      break
    case 'Coinbase':
      provider =
        providers.find((_provider) => _provider.isCoinbaseWallet) ||
        (ethereum.isCoinbaseWallet && ethereum)
      break
    case 'Clover':
      provider =
        providers.find((_provider) => _provider.isClover) ||
        (window.clover && window.clover.isClover && window.clover)
      break
    case 'BitKeep':
      provider =
        providers.find((_provider) => _provider.isBitKeep) ||
        (window.bitkeep && window.bitkeep.ethereum)
      break
    case 'OKX':
      provider =
        providers.find((_provider) => _provider.isOkxWallet) ||
        (window.okxwallet && window.okxwallet.isOkxWallet && window.okxwallet)
      break
    case 'MetaMask':
      provider =
        providers.find(
          (_provider) => _provider.isMetaMask && !_provider.isBitKeep && !_provider.isTokenPocket
        ) ||
        (ethereum.isMetaMask && !ethereum.isBitKeep && !ethereum.isTokenPocket && ethereum)
      break
    default:
      break
  }

  return provider
}

