/**
 * 绑定事件：每个事件仅绑定一个回调，重复绑定仅最后一个生效
 *
 * 主要用于全局单例事件绑定：
 * 1. ethereum：MetaMask提供的全局对象
 * 2. walletConnectProvider：WalletConnectProvider实例，这里实现为单例
 * 3. clover：Clover提供的全局对象
 *
 * @param {object} target 目标对象
 * @param {string} eventName 待监听的事件名
 * @param {function} callback 事件回调
 */
function uniqueOn(target, eventName, callback) {
  const callbackName = 'unique' + eventName.slice(0, 1).toUpperCase() + eventName.slice(1)
  if (target[callbackName]) {
    target.removeListener(eventName, target[callbackName])
  }
  target[callbackName] = callback
  target.on(eventName, target[callbackName])
}

export function bind({ accountsChanged, chainChanged }) {
  function _accountsChanged(accounts) {
    console.log('accountsChanged', accounts)
    _account = (accounts && accounts[0]) || ''
    typeof accountsChanged === 'function' && accountsChanged(accounts)
  }
  function _chainChanged(chainId) {
    console.log('chainChanged', chainId)
    typeof chainChanged === 'function' && chainChanged(chainId)
  }

  WALLET.forEach((wallet) => {
    const provider = getWalletProvider(wallet.name)
    if (provider) {
      uniqueOn(provider, 'accountsChanged', _accountsChanged)
      uniqueOn(provider, 'chainChanged', _chainChanged)
    }
  })
}

export async function connect(walletName) {
  if (!walletName) {
    walletName = localStorage.getItem('CONNECTED_WALLET') || (WALLET[0] && WALLET[0].name)
  }
  localStorage.setItem('CONNECTED_WALLET', walletName)

  const provider = getWalletProvider(walletName)
  if (typeof provider === 'undefined') {
    alert(`Please install ${walletName}!`)
    throw new Error(`${walletName} is not installed!`)
  }

  _web3 = new Web3(provider)

  const accounts = await provider.request({ method: 'eth_requestAccounts' })
  console.log('connect', accounts)
  _account = accounts[0]
  return _account
}

/**
 * 钱包签名（调用前确保钱包已连接）
 * @param {String} address
 * @returns
 */
export async function signWallet(address) {
  const nonce = `login_${Date.now()}_${String(Math.random()).slice(-8)}`
  const sign = await _web3.eth.personal.sign(nonce, address)
  return { nonce, sign }
}

export async function sign(address) {
  address = address || _account
  if (!address) {
    throw new Error('address is required!')
  }

  const { nonce, sign } = await signWallet(address)
  const path = '/users/personal_sign_auth'

  const params = new URLSearchParams()
  params.append('user_addr', address)
  params.append('nonce', nonce)
  params.append('sign', sign)

  return await _axios.post(path, params)
}

export async function bindWallet(address) {
  address = address || _account
  if (!address) {
    throw new Error('address is required!')
  }

  const { nonce, sign } = await signWallet(address)
  const path = '/account/bind_user_address'

  const params = new URLSearchParams()
  params.append('user_addr', address)
  params.append('nonce', nonce)
  params.append('sign', sign)

  return await _axios.post(path, params)
}

export async function unbindWallet(address) {
  address = address || _account
  if (!address) {
    throw new Error('address is required!')
  }

  const { nonce, sign } = await signWallet(address)
  const path = '/account/unbind_user_address'

  const params = new URLSearchParams()
  params.append('user_addr', address)
  params.append('nonce', nonce)
  params.append('sign', sign)

  return await _axios.post(path, params)
}

