/**
 * 是否支持传入的区块链
 * @param {Number} chainId 链ID
 */
export function isSupportedChain(chainId) {
  return (
    [
      0x1, // ETH
      0x38, // BSC
    ].indexOf(parseInt(chainId)) > -1
  )
}
